console.log("connected");
window.onload = function () {
  var navBar = document.getElementById("nav");
  var button = document.getElementById("toggler");

  // console.log(navBar);
  // console.log(button);

  button.addEventListener("click", () => {
    navBar.classList.toggle("active");
    //   alert("clicked");
  });
};
